x = load('datos.txt'); orden=input('Ingrese el orden (con numero): ');  %%vectorXorden= zeros(1,orden+1); % vector x ordenado   %%vectorYorden= zeros(1,orden+1); % vector y ordenado  valorX=input('Ingresa el valor a encontrar: ');  len=length(x);  %% ordenar en un nuevo vector los valores de x0,x21,x2,...xn y f(x0),f(x1),...f(xn)  %% encontrar a su vex sus extremos( posiciones) de elementos a ocupar con respecto a x respecto a filas  for  i=2:1:(orden+1)      if (i==2)                  for j=2:1:len                if (valorX<x(j,1) && valorX>x(j-1,1));                      primero=abs( x(j-1,1)-valorX );                      segundo=abs( x(j,1)-valorX );                                    extremos(1,1)=j-1;                      extremos(1,2)=j;                      if (primero<segundo)                          vectorYorden(1)=x(j-1,2);                          vectorXorden(1)=x(j-1,1);                          vectorYorden(2)=x(j,2);                          vectorXorden(2)=x(j,1);                      else                          vectorYorden(1)=x(j,2);                          vectorXorden(1)=x(j,1);                          vectorYorden(2)=x(j-1,2);                          vectorXorden(2)=x(j-1,1);                      endif                endif          endfor      else            if (extremos(1)!=1 && extremos(2)!=len)                  primero=abs( x((extremos(1)-1),1 )-valorX);                  segundo=abs( x((extremos(2)+1),1 )-valorX);                  if (primero<segundo)                          extremos(1,1)=extremos(1,1)-1;                          vectorYorden(i)=x(extremos(1),2);                          vectorXorden(i)=x(extremos(1),1);                                        else                          extremos(2)=extremos(2)+1;                          vectorYorden(i)=x(extremos(2),2);                          vectorXorden(i)=x(extremos(2),1);                                       endif            elseif(extremos(1)==1)                  extremos(2)=extremos(2)+1;                  vectorYorden(i)=x(extremos(2),2);                  vectorXorden(i)=x(extremos(2),1);            elseif (extremos(2)==len)                  extremos(1)=extremos(1)-1;                  vectorYorden(i)=x(extremos(1),2);                  vectorXorden(i)=x(extremos(1),1);            endif               endif 
endfor
%% calculando valores de bo,1, b2 ...bn y poniendolos en el vectorvectorb(1)=vectorYorden(1);for i=1:1:orden      for j=1:1:(orden+1-i)        temp=(vectorYorden(j+1)-vectorYorden(j))/(vectorXorden(j+i)-vectorXorden(j));        vectortemporal(j)=temp;    endfor    vectorb(i+1)=vectortemporal(1);    vectorYorden(orden+2-i)=0;    for k=1:1:orden        vectorYorden(k)=vectortemporal(k);    endfor    vectortemporal(orden+1-i)=0;endforvectorb% valor encontrado  resp=0; resp=vectorb(1);for i=1:1:(orden)     parteMulti=0;     for j=1:1:i        if(j==1)              parteMulti=(valorX-vectorXorden(j));        else               parteMulti*=(valorX-vectorXorden(j));        endif     endfor     resp=resp+(vectorb(i+1)*parteMulti);  endforprintf('la respuesta es %f',resp);